mapper.select:
SELECT a.id AS id, b.question_source AS questionSource, a.q_theme AS theme, a.q_type AS questionType, c.name AS brandType, a.record_time AS recordTime, a.q_degree AS degree, b.redirect_user_id AS redirectUserId FROM mtmydb.fcsd_question a LEFT JOIN mtmydb.fcsd_question_solve b ON a.id=b.question_id LEFT JOIN mtmydb.mtmy_brand c ON a.brand_type=c.band_id WHERE 1=1
and redirect_user_id=#{redirectUserId}
and is_member=#{member}
and b.redirect_user_id=s_user_id
and q_status=#{status}
ORDER BY b.create_date DESC LIMIT 6

SELECT count(a.id) FROM mtmydb.fcsd_question a LEFT JOIN mtmydb.fcsd_question_solve b ON a.id=b.question_id WHERE 1=1
and is_member=#{member}
and redirect_user_id=#{redirectUserId}
and b.redirect_user_id=s_user_id
and q_status=#{status}

SELECT q.id AS id, q.is_member AS member, q.q_theme AS theme, q.q_type AS questionType, v.name AS brandType, q.nickname AS nickName, q.name AS name, q.create_date AS creatDate, q.record_time AS recordTime, q.q_degree AS degree, h.name AS creatBy, (SELECT u.name FROM mtmydb.fcsd_question_solve k LEFT JOIN trains.sys_user u ON u.id = k.s_user_id WHERE k.question_id = q.id ORDER BY k.create_date DESC LIMIT 1) AS handler, q.q_status AS status, (SELECT f.redirect_explain FROM mtmydb.fcsd_question_solve f WHERE f.question_id = q.id ORDER BY f.create_date DESC LIMIT 1) AS changeTimes, question_source AS questionSource FROM mtmydb.fcsd_question q LEFT JOIN mtmydb.fcsd_question_solve s ON q.id = s.question_id LEFT JOIN trains.sys_user h ON h.id = q.create_by LEFT JOIN mtmydb.mtmy_brand v ON q.brand_type=v.band_id WHERE 1=1
and q.brand_type=#{brandType}
and q.q_status=#{status}
and q.is_member=#{member}
AND (q.name LIKE CONCAT('%', #{name}, '%') OR q.nickname LIKE CONCAT('%', #{name}, '%'))
and s.redirect_user_id=s_user_id
and s.redirect_user_id=#{redirectUserId}
and (q.mobile=#{mobile} or q.nickname=#{mobile})
ORDER BY q.id DESC

SELECT question_degree AS questionDegree, s_result AS handResult, a.create_date AS cretDate, b.name AS handler, b.no AS handlerID, s_content AS solveContent, c.name AS redirectUserId, remark AS remarks, a.redirect_explain AS changeTimes, s_times AS solveTimes FROM mtmydb.fcsd_question_solve a LEFT JOIN trains.sys_user b ON a.s_user_id = b.id LEFT JOIN trains.sys_user c ON a.redirect_user_id = c.id WHERE 1=1
and question_id=#{id}
and s_times !=0 ORDER BY a.s_times DESC

SELECT a.id AS id, nickname AS nickName, a.name AS name, a.mobile AS mobile, brand_type AS brandType, q_theme AS theme, consume_shop AS consumeShop, q_type AS questionType, q_degree AS degree, q_content AS content, call_back AS callBack, q_status AS status, record_time AS recordTime, a.mtmy_user_id AS member, a.create_date AS creatDate, b.name AS creatBy, c.question_source AS questionSource, (SELECT f.redirect_explain FROM mtmydb.fcsd_question_solve f WHERE f.question_id = a.id ORDER BY f.create_date DESC LIMIT 1) AS changeTimes FROM mtmydb.fcsd_question a LEFT JOIN trains.sys_user b ON a.create_by = b.id LEFT JOIN mtmydb.fcsd_question_solve c ON a.id = c.question_id WHERE 1=1
and a.id=#{id}
ORDER BY c.create_date DESC LIMIT 1
and c.redirect_user_id=#{redirectUserId}

SELECT redirect_user_id AS redirectUserId, id AS id FROM mtmydb.fcsd_question_solve WHERE 1=1
and question_id=#{id}
and redirect_user_id = #{redirectUserId};

SELECT id AS solveId, redirect_explain AS changeTimes, s_times AS solveTimes, question_source AS questionSource FROM mtmydb.fcsd_question_solve WHERE 1=1
and question_id=#{id}
and id=(SELECT max(id) FROM mtmydb.fcsd_question_solve where question_id = #{id})

mapper.update:
UPDATE mtmydb.fcsd_question_solve 
SET question_source=#{questionSource}
WHERE 
    id=#{solveId}

UPDATE mtmydb.fcsd_question SET
	 q_status=2
where id=#{id}
