mapper.select:
SELECT mc.user_id AS userId, mu.`name`, mu.sex, mu.mobile, mu.nickname, mu.level_value AS 'levelvalue', mu.reg_time regTime, MIN(mo.add_time) firstDate, MAX(mo.add_time) lastDate, muyl.`level` AS 'level', mc.franchisee_id AS 'franchiseeId', tsf. NAME AS 'franchiseeName', tsu.`name` AS 'beautyName', tso.`name` AS 'shopName' FROM mtmydb.mtmy_customer mc LEFT JOIN trains.sys_franchisee tsf ON mc.franchisee_id = tsf.id LEFT JOIN trains.sys_user tsu ON mc.beauty_id = tsu.id LEFT JOIN trains.sys_office tso ON mc.office_id = tso.id LEFT JOIN mtmydb.mtmy_users mu ON mc.user_id = mu.user_id LEFT JOIN mtmydb.mtmy_orders mo ON mc.user_id = mo.user_id LEFT JOIN mtmydb.mtmy_user_yz_level muyl ON mu.level_value >= muyl.min_val AND mu.level_value <= muyl.max_val ${sqlMap.dsf}
AND mc.franchisee_id = #{franchiseeId}
AND ( locate( #{officeId}, concat(mc.office_pids, mc.office_id) ) > 0 )
AND muyl.level =#{level}
AND ( mu.nickname like CONCAT('%', #{keyword} , '%') OR mu.mobile like CONCAT('%', #{keyword} , '%') OR mu.name like CONCAT('%', #{keyword} , '%') )
GROUP BY mc.user_id ORDER BY mu.reg_time DESC
