SELECT mu.user_id AS userId, mu.`name`, mu.sex, mu.mobile, mu.nickname, mu.level_value AS 'levelvalue', mu.reg_time regTime, mua.account_arrearage accountArrearage, MIN(mo.add_time) firstDate, MAX(mo.add_time) lastDate, muyl.`level` AS 'level', tsu. NAME AS 'beautyName', tso. NAME AS 'shopName' FROM mtmydb.mtmy_users mu LEFT JOIN mtmydb.mtmy_user_accounts mua ON mu.user_id = mua.user_id LEFT JOIN mtmydb.mtmy_orders mo ON mu.user_id = mo.user_id LEFT JOIN mtmydb.mtmy_user_yz_level muyl ON mu.level_value >=muyl.min_val AND mu.level_value <=muyl.max_val LEFT JOIN trains.sys_user tsu ON mu.beautician_id = tsu.id LEFT JOIN trains.sys_office tso ON mu.office_id = tso.id ${sqlMap.dsf}
AND (tso.id = #{officeId} OR tso.parent_ids LIKE CONCAT('%,', #{officeId}, ',%'))
AND muyl.level =#{level}
AND ( mu.nickname like CONCAT('%', #{keyword} , '%') OR mu.mobile like CONCAT('%', #{keyword} , '%') OR mu.name like CONCAT('%', #{keyword} , '%') OR tsu.name like CONCAT('%', #{keyword} , '%') )
GROUP BY mu.user_id, muyl.NAME, muyl.`level` ORDER BY mu.reg_time DESC
