mapper.select:
SELECT a.id, a.user_id, f.id as "actionId", f.`name` as "actionName", a.coupon_id, a. STATUS, a.add_time, a.used_time, a.order_id, b.`nickname` AS "name", c.order_amount AS "orderAmount", d.coupon_name, d.coupon_type AS "type", d.coupon_money FROM mtmydb.mtmy_activity_coupon_user a
WHERE 1=1
AND a.coupon_id=#{couponId}
AND a.order_id=#{orderId}
AND b.nickname like '%'||#{name}||'%' CONCAT('%',#{name}, '%')
AND a.status=#{status}
AND b.mobile=#{mobile}
AND DATE_FORMAT(a.add_time,'%Y-%m-%d') >= DATE_FORMAT(#{begtime},'%Y-%m-%d')
AND DATE_FORMAT(a.add_time,'%Y-%m-%d') <= DATE_FORMAT(#{endtime},'%Y-%m-%d')
ORDER BY a.add_time DESC

SELECT a.id as "mxId", a.user_id as "userId", d.nickname as "name", d.mobile as "mobile", a.coupon_id as "couponId", c.coupon_name as "couponName", c.coupon_money as "couponAmount", CASE c.used_type WHEN 1 THEN '全部商品' WHEN 2 THEN '指定分类' ELSE '指定商品' END as "usedType", CASE a.`status` WHEN 0 THEN '未使用' WHEN 1 THEN '已使用' ELSE '已过期' 0END as "excelStatus", a.add_time as "addTime", a.used_time as "usedTime", a.order_id as "orderId", b.order_amount as "orderMoney", f.id as "actionId", f.`name` as "actionName" FROM mtmydb.mtmy_activity_coupon_user a LEFT JOIN mtmydb.mtmy_orders b ON a.order_id=b.order_id LEFT JOIN mtmydb.mtmy_activity_coupon c ON a.coupon_id=c.id LEFT JOIN mtmydb.mtmy_users d ON d.user_id=a.user_id LEFT JOIN mtmydb.mtmy_activity f ON f.id=c.action_id WHERE 1=1
AND a.coupon_id=#{couponId}
AND a.order_id=#{orderId}
AND d.nickname like '%'||#{name}||'%' CONCAT('%',#{name}, '%')
AND a.status=#{status}
AND DATE_FORMAT(a.add_time,'%Y-%m-%d') >= DATE_FORMAT(#{begtime},'%Y-%m-%d')
AND DATE_FORMAT(a.add_time,'%Y-%m-%d') <= DATE_FORMAT(#{endtime},'%Y-%m-%d')
ORDER BY a.add_time DESC

mapper.update:
UPDATE mtmydb.mtmy_activity_coupon_user SET
status =#{status}
WHERE order_id = #{orderId}
