mapper.select:
SELECT , c.name AS "category.name" FROM article_repository AS a LEFT JOIN article_category c ON a.category_id = c.id WHERE 1=1 AND a.del_flag = #{DEL_FLAG_NORMAL}
AND a.category_id = #{categoryId}
AND a.title like '%'||#{title}||'%' CONCAT('%', #{title}, '%')
AND DATE_FORMAT(a.create_date,'%Y-%m-%d') >= DATE_FORMAT(#{beginDate},'%Y-%m-%d')
AND DATE_FORMAT(a.create_date,'%Y-%m-%d') <= DATE_FORMAT(#{endDate},'%Y-%m-%d')
AND a.type = #{articleType}
ORDER BY a.create_date DESC

mapper.update:
UPDATE article_repository SET 
	update_by   = #{updateBy.id},
	update_date = SYSDATE(), 
	del_flag	= #{DEL_FLAG_DELETE}
WHERE id = #{articleId}

UPDATE mtmy_articles SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE article_category SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{categoryId}
