mapper.select:
SELECT banner_id AS 'bannerId', banner_name AS 'bannerName', img_url AS 'imgUrl', banner_type AS 'bannerType', type AS 'pageType', sort, is_open AS 'isOpen', franchisee_ids AS 'franchiseeIds', is_show AS 'isShow', is_share AS 'isShare' FROM mtmydb.mtmy_banner WHERE del_flag = 0
AND banner_name LIKE CONCAT('%', #{bannerName}, '%')
AND is_show = #{isShow}
AND is_share = #{isShare}
AND banner_type = #{bannerType}
AND (LOCATE(#{franchiseeIds},franchisee_ids ) > 0 )
ORDER BY sort DESC

mapper.update:
UPDATE mtmydb.mtmy_banner
SET

	is_show = #{isShow}


	is_share = #{isShare}

WHERE banner_id = #{bannerId}

UPDATE mtmydb.mtmy_banner 
SET del_flag = 1
WHERE banner_id = #{bannerId}
