mapper.select:
SELECT banner_id AS 'bannerId', banner_name AS 'bannerName', img_url AS 'imgUrl', banner_type AS 'bannerType', type AS 'pageType', sort, is_open AS 'isOpen', franchisee_ids AS 'franchiseeIds', del_flag AS 'isShow', is_share AS 'isShare' FROM mtmydb.mtmy_banner WHERE 1 = 1
AND del_flag = #{isShow}
AND is_share = #{isShare}
AND banner_type = #{bannerType}
ORDER BY sort DESC

mapper.update:
UPDATE mtmydb.mtmy_banner
SET

	del_flag = #{isShow}


	is_share = #{isShare}

WHERE banner_id = #{bannerId}
