mapper.update:
UPDATE
	mtmydb.mtmy_comment
SET
    is_show = #{isShow}
WHERE 
	comment_id = #{parentId}
	OR parent_id = #{parentId}

mapper.select:
SELECT t.*,tt.name AS 'user.name' FROM (SELECT , u.nickname AS 'users.name' FROM mtmydb.mtmy_beauty_comment AS b LEFT JOIN mtmydb.mtmy_users AS u ON b.user_id = u.user_id WHERE 1=1 AND b.parent_id = '0'
AND b.add_time >= #{beginDate}
AND b.add_time <= #{endDate}
AND u.nickname LIKE '%'||#{usersName}||'%' CONCAT('%', #{usersName}, '%')
AND b.img != '' AND b.img is not null
AND (b.img = '' OR b.img is null)
) t join sys_user tt on t.beautyId = tt.id WHERE 1 = 1
AND tt.name LIKE '%'||#{userName}||'%' CONCAT('%', #{userName}, '%')
ORDER BY t.addTime DESC

mapper.update:
UPDATE
	mtmydb.mtmy_beauty_comment
SET
    is_show = #{isShow}
WHERE 
	comm_id = #{parentId}
	OR parent_id = #{parentId}

mapper.select:
SELECT , b.name AS shopName, b.name AS 'office.name', d.nickname AS 'users.name' FROM mtmydb.mtmy_shop_comment AS a LEFT JOIN trains.sys_office AS b ON b.id = a.shop_id LEFT JOIN mtmydb.mtmy_appt_order AS c ON a.appt_order_id = c.id LEFT JOIN mtmydb.mtmy_users AS d ON a.user_id = d.user_id WHERE 1=1 AND a.parent_id = '0'
AND a.create_date >= #{beginDate}
AND a.create_date <= #{endDate}
AND d.nickname LIKE '%'||#{userName}||'%' CONCAT('%', #{userName}, '%')
AND a.shop_id = #{office.id}
AND a.img != '' AND a.img is not null
AND (a.img = '' OR a.img is null)
GROUP BY a.id ORDER BY a.create_date DESC

mapper.update:
UPDATE
	mtmydb.mtmy_shop_comment
SET
    is_show = #{isShow}
WHERE 
	id = #{parentId}
	OR parent_id = #{parentId}
