mapper.update:
UPDATE mtmydb.mtmy_customer SET
beauty_id = #{beautyId}, beauty_time = SYSDATE(),
office_id = #{officeId}, office_pids = #{officePids}, office_time = SYSDATE() WHERE user_id = #{userId} AND franchisee_id = #{franchiseeId}

mapper.select:
SELECT COUNT(1)
FROM mtmydb.mtmy_customer
WHERE user_id = #{userId}
AND franchisee_id = #{franchiseeId}

SELECT 
	cus_id as 'cusId',
	office_id as 'officeId'
FROM mtmydb.mtmy_customer
WHERE user_id = #{userId}
AND franchisee_id = #{franchiseeId}
