mapper.select:
SELECT , b.name AS franchiseeName FROM mtmydb.mtmy_franchisee_banner a LEFT JOIN mtmydb.mtmy_franchisee b ON a.franchisee_id = b.id WHERE 1 = 1
AND is_show = #{isShow}
AND a.del_flag = 0 ORDER BY a.id DESC

mapper.update:
UPDATE mtmydb.mtmy_franchisee_banner
	SET 
	  is_show = #{isShow}
WHERE id = #{id}
