mapper.select:
SELECT g.goods_id AS 'goodsId', g.goods_name AS 'goodsName', g.goods_sn AS 'goodsSn', c.name AS 'goodsCategory.name', g.shop_price AS 'shopPrice', g.total_store AS 'totalStore', g.store_count AS 'storeCount', g.is_on_sale AS 'isOnSale', g.is_recommend AS 'isRecommend', g.is_new AS 'isNew', g.is_hot AS 'isHot', g.is_appshow AS 'isAppshow', g.is_real AS 'isReal', g.sort, g.action_id as "actionId", b.action_name as "actionName" FROM mtmydb.mtmy_goods g LEFT JOIN mtmydb.mtmy_goods_category c ON c.category_id = g.category_id LEFT JOIN mtmydb.mtmy_action_info b ON b.action_id=g.action_id WHERE 1 = 1
AND g.action_id=#{actionId}
AND g.category_id = #{goodsCategoryId}
AND g.goodsbrand_id = #{goodsBrandId}
AND g.is_on_sale = #{isOnSale}
AND g.is_new = #{isNew}
AND g.is_recommend = #{isRecommend}
AND g.is_hot = #{isHot}
AND g.is_appshow = #{isAppshow}
AND g.is_real = #{isReal}
AND (g.goods_name LIKE CONCAT('%', #{querytext}, '%') OR g.keywords LIKE CONCAT('%', #{querytext}, '%'))
AND g.del_flag = 0 ORDER BY g.sort DESC

SELECT
FROM mtmydb.mtmy_goods g
WHERE g.del_flag = 0 AND g.goods_id = #{goodsId}

SELECT
FROM mtmydb.mtmy_goods g
WHERE g.del_flag = 0 AND g.action_id = #{actionId}

SELECT 

FROM
  mtmydb.mtmy_goods g

WHERE g.del_flag = 0 

	AND g.action_id=0


	AND g.franchisee_id=#{franchiseeId}


	AND g.category_id=#{goodsCategoryId} 


	AND g.action_type=#{actionType}


	AND g.goods_name like
	'%'||#{goodsName} ||'%'
	CONCAT('%', #{goodsName} , '%')


	AND g.is_real = #{isReal}


	AND g.is_on_sale = #{isOnSale}


	AND g.is_appshow = #{isAppshow}

mapper.insert:
