0
#{advancePrice}
)

mapper.update:
UPDATE mtmydb.mtmy_goods SET category_id = #{goodsCategoryId}, franchisee_id = #{franchiseeId}, goodsbrand_id = #{goodsBrandId}, goods_sn = #{goodsSn}, goods_short_name = #{goodsShortName}, goods_description = #{goodsDescription}, goods_key = #{goodsKey}, goods_name = #{goodsName}, weight = #{weight}, market_price = #{marketPrice}, shop_price = #{shopPrice}, cost_price = #{costPrice}, goods_tags = #{goodsTags}, keywords = #{keywords}, goods_remark = #{goodsRemark}, goods_content = #{goodsContent}, original_img = #{originalImg}, is_real = #{isReal}, is_on_sale = #{isOnSale}, is_free_shipping = #{isFreeShipping}, sort = #{sort}, is_recommend = #{isRecommend}, is_new = #{isNew}, is_hot = #{isHot}, is_appshow = #{isAppshow}, attr_type = #{attrType}, spec_type = #{specType}, give_integral = #{giveIntegral}, sales_sum = #{salesSum}, region_name = #{regionName}, service_min = #{serviceMin}, action_type = #{actionType}, last_update = SYSDATE(), goods_type = #{goodsType},
advance_price = 0
advance_price = #{advancePrice}
WHERE del_flag = 0 AND goods_id = #{goodsId}

UPDATE mtmydb.mtmy_goods
	SET total_store=0
WHERE del_flag = 0 AND goods_id = #{goodsId}

UPDATE mtmydb.mtmy_goods SET
	 total_store = #{totalStore},
	 store_count = #{storeCount}
WHERE del_flag = 0 AND goods_id = #{goodsId}

