mapper.select:
SELECT
	,
	ac.name AS categoryName,
	(SELECT COUNT(*) FROM mtmydb.mtmy_articles_comment AS c WHERE c.articles_id=a.id AND c.del_flag = 0 AND a.del_flag=0) AS comNum
FROM mtmydb.mtmy_articles AS a
LEFT JOIN mtmydb.mtmy_article_category ac ON ac.id = a.category_id
	WHERE 1=1

			AND a.del_flag = #{delFlag}


			AND a.audit_flag = #{auditFlag}
			AND a.del_flag = #{DEL_FLAG_NORMAL}


			AND a.category_id = #{categoryId}


			AND a.title like 
					'%'||#{title}||'%'
					CONCAT('%', #{title}, '%')


			AND a.create_date BETWEEN #{beginDate} AND #{endDate}

		ORDER BY a.is_top,a.sort DESC,a.create_date DESC

mapper.update:
UPDATE mtmydb.mtmy_articles_comment SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE mtmydb.mtmy_articles SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE mtmydb.mtmy_article_category SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT a.title, u.name AS 'users.name', u.nickname AS 'users.nickname', u.mobile AS 'users.mobile', c.contents AS 'contents' FROM mtmydb.`mtmy_articles_comment` AS c LEFT JOIN mtmydb.mtmy_articles AS a ON c.articles_id = a.id LEFT JOIN mtmydb.`mtmy_users` AS u on c.`create_by` = u.`user_id` WHERE c.`contents` LIKE '%报名%'
AND c.create_date BETWEEN #{beginDate} AND #{endDate}
ORDER BY c.create_date DESC

mapper.update:
UPDATE mtmydb.mtmy_articles SET
	is_top = #{isTop}
WHERE id = #{id}

UPDATE mtmydb.mtmy_articles SET
	is_recommend = #{isRecommend}
WHERE id = #{id}

UPDATE mtmydb.mtmy_articles SET
	is_show = #{isShow}
WHERE id = #{id}
