mapper.select:
SELECT 

FROM mtmydb.mtmy_oa_notify a

WHERE a.id = #{id}

SELECT 

FROM mtmydb.mtmy_oa_notify a

WHERE a.del_flag = #{DEL_FLAG_NORMAL}

	AND a.TITLE LIKE 
			'%'||#{title}||'%'
			CONCAT('%', #{title}, '%')


	AND a.TYPE = #{type}


	AND a.STATUS = #{status}

ORDER BY a.update_date DESC

mapper.update:
UPDATE mtmydb.mtmy_oa_notify SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

update  mtmydb.mtmy_oa_notify
set status = #{status}
where id = #{notify_id}
