mapper.update:
UPDATE mtmydb.mtmy_sale_relations SET
	status = #{status}
where 
	user_id = #{userId} 
	AND parent_id = #{parentId}

mapper.select:
SELECT r.depth, u.user_id AS "users.userid", u.layer AS "users.layer", u.nickname AS "users.nickname", u.mobile AS "users.mobile", r.parent_id AS parentId, uu.layer AS "newusers.layer", uu.nickname AS "newusers.nickname", uu.mobile AS "newusers.mobile",
r.create_date, l.balanceAmount AS "mtmySaleRelieveLog.balanceAmount", l.integralAmount AS "mtmySaleRelieveLog.integralAmount", (SELECT COUNT(1) FROM mtmydb.mtmy_sale_relations WHERE parent_id = u.user_id) AS lowerNum FROM mtmydb.mtmy_sale_relations AS r LEFT JOIN ( SELECT rebate_user,receive_user,SUM(balance_amount) AS balanceAmount,SUM(integral_amount) AS integralAmount FROM mtmydb.mtmy_day_account WHERE rebate_flag = 1 GROUP BY rebate_user,receive_user ) AS l ON (r.user_id = l.rebate_user AND r.parent_id = l.receive_user) LEFT JOIN mtmydb.mtmy_users AS u ON r.user_id = u.user_id LEFT JOIN mtmydb.mtmy_users AS uu ON r.parent_id = uu.user_id
WHERE r.parent_id = #{users.userid }
ORDER BY ${page.orderBy}
ORDER BY r.create_date,r.depth

SELECT l.create_date, u.nickname AS "users.nickname", u.layer AS "users.layer", l.balance_amount AS balanceAmount, l.integral_amount AS integralAmount, l.rebate_flag AS rebateFlag, l.order_id AS "orders.orderid" FROM mtmydb.mtmy_sale_rebates_log AS l LEFT JOIN mtmydb.mtmy_users AS u ON u.user_id = l. rebate_user WHERE l.rabate_date = #{rabateDate} AND l.receive_user = #{receiveUser}
AND l.rebate_flag != 2
AND l.rebate_flag = 2

SELECT u.user_id AS "users.userid", u.layer AS "users.layer", u.nickname AS "users.nickname", u.mobile AS "users.mobile", m.nickname AS "newusers.nickname", m.layer AS "newusers.layer", m.mobile AS "newusers.mobile", r.depth, r.create_date, uu.user_balance AS "users.usermoney", uu.user_integral AS "users.paypoints", r.status,
TRUNCATE((b.balance_amount + c.balance_amount),2) AS "mtmySaleRelieveLog.balanceAmount", TRUNCATE((b.integral_amount + c.integral_amount),2) AS "mtmySaleRelieveLog.integralAmount", rc.ABnum FROM mtmydb.mtmy_sale_relations AS r LEFT JOIN mtmydb.mtmy_users AS u ON r.user_id = u.user_id LEFT JOIN mtmydb.mtmy_user_accounts AS uu ON uu.user_id = u.user_id LEFT JOIN mtmydb.mtmy_users AS m ON r.parent_id = m.user_id
LEFT JOIN (SELECT IFNULL(receive_user,#{users.userid }) AS receive_user,IFNULL(SUM(balance_amount),0) AS balance_amount,IFNULL(SUM(integral_amount),0) AS integral_amount FROM mtmydb.mtmy_day_account WHERE receive_user = #{users.userid } AND rebate_flag = 0 ) AS b ON b.receive_user = r.user_id LEFT JOIN (SELECT IFNULL(receive_user,#{users.userid }) AS receive_user,IFNULL(SUM(balance_amount),0) AS balance_amount,IFNULL(SUM(integral_amount),0) AS integral_amount FROM mtmydb.mtmy_sale_rebates_log WHERE receive_user = #{users.userid } AND rebate_flag = 0) AS c ON c.receive_user = r.user_id LEFT JOIN (SELECT COUNT(parent_id) AS ABnum,parent_id FROM mtmydb.mtmy_sale_relations WHERE 1 = 1 GROUP BY parent_id) AS rc ON rc.parent_id = r.user_id WHERE 1=1 AND r.user_id = #{users.userid}
