mapper.update:
UPDATE mtmydb.mtmy_users SET
user_money =user_money+#{usermoney}
WHERE user_id = #{userid}

UPDATE mtmydb.mtmy_users SET
pay_points=pay_points+#{paypoints},
level_value=level_value+#{levelValue}
WHERE user_id = #{userid}

mapper.select:
SELECT 

FROM
  mtmydb.mtmy_users  u 
WHERE 
	1=1

		AND u.nickname LIKE 
			'%'||#{nickname}||'%'
			CONCAT('%', #{nickname}, '%')


		AND u.user_id = #{userid} 


		AND u.mobile LIKE 
			'%'||#{mobile}||'%'
			CONCAT('%', #{mobile}, '%')


		AND u.level >= #{level} 


		AND (
			u.district = #{district }
			or u.city = #{district }
			or u.province = #{district }
			)


	AND u.del_flag = #{DEL_FLAG_NORMAL}
ORDER BY u.reg_time DESC

SELECT layer
FROM mtmydb.mtmy_users
WHERE mobile = #{mobile}

mapper.update:
UPDATE mtmydb.mtmy_users
SET layer = 'A'
WHERE user_id = #{userId}

UPDATE mtmydb.mtmy_users    
SET is_test = #{isTest}
WHERE user_id = #{userId}
