mapper.select:
SELECT a.order_id AS orderid, a.order_status AS orderstatus, a.pay_name AS payname, a.user_money AS usermoney, a.coupon_price AS couponprice, a.order_amount AS orderamount, a.total_amount AS totalamount, a.add_time AS addtime, a.action_id AS actionid, a.user_note AS userNote, a.admin_note AS adminnote, a.create_logo AS createlogo, a.postal_code AS postalcode, a.shipping_type AS shippingtype, a.flag AS flag, a.mobile AS mobile, u.nickname AS "username", a.is_real as isReal, a.channel_flag AS channelflag, (select COUNT(gm.rec_id) from mtmydb.mtmy_order_goods_mapping gm where gm.order_id = a.order_id and gm.group_id = 0 ) AS "goodsnum",
a.del_flag, (CASE a.order_status WHEN '-2' THEN a.cancel_type ELSE '' END) AS cancelType, IFNULL(u.office_id,'') as officeId, u.user_id as userid FROM mtmydb.mtmy_orders a LEFT JOIN mtmydb.mtmy_users u ON a.user_id = u.user_id
LEFT JOIN mtmydb.mtmy_order_goods_mapping m ON a.order_id = m.order_id LEFT JOIN mtmydb.mtmy_goods g ON g.goods_id=m.goods_id LEFT JOIN mtmydb.mtmy_goods_category f ON f.category_id=g.category_id ${sqlMap.dsf} and a.parent_id=0
AND u.nickname like '%'||#{username} ||'%' CONCAT('%', #{username} , '%')
AND u.mobile=#{mobile}
AND a.order_id = #{orderid}
AND a.order_status=#{orderstatus}
AND a.create_logo=#{createlogo}
AND a.channel_flag=#{channelFlag}
AND DATE_FORMAT(a.add_time,'%Y-%m-%d') >= DATE_FORMAT(#{begtime},'%Y-%m-%d')
AND DATE_FORMAT(a.add_time,'%Y-%m-%d') <= DATE_FORMAT(#{endtime},'%Y-%m-%d')
AND DATE_FORMAT(a.pay_time,'%Y-%m-%d') >= DATE_FORMAT(#{payBegTime},'%Y-%m-%d')
AND DATE_FORMAT(a.pay_time,'%Y-%m-%d') <= DATE_FORMAT(#{payEndTime},'%Y-%m-%d')
AND a.user_id = #{userid}
AND f.category_id=#{cetaid}
AND m.goods_id=#{goodsids}
AND f.category_id=#{cetaid} and m.goods_id=#{goodsids}
AND a.is_real=#{searchIsReal}
AND a.del_flag=#{userDelFlag}
AND a.is_neworder = #{newIsNeworder}
GROUP BY a.order_id
ORDER BY ${page.orderBy}
ORDER BY a.add_time DESC

SELECT a.order_id AS orderid, a.order_status AS orderstatus, a.pay_name AS payname, a.user_money AS usermoney, a.coupon_price AS couponprice, a.order_amount AS orderamount, a.total_amount AS totalamount, a.add_time AS addtime, a.action_id AS actionid, a.user_note AS userNote, a.admin_note AS adminnote, a.create_logo AS createlogo, a.postal_code AS postalcode, a.shipping_type AS shippingtype, a.flag AS flag, a.mobile AS mobile, u.nickname AS "username", a.is_real as isReal, a.channel_flag AS channelflag, (select COUNT(gm.rec_id) from mtmydb.mtmy_order_goods_mapping gm where gm.order_id = a.order_id and gm.group_id = 0 ) AS "goodsnum",
a.del_flag, (CASE a.order_status WHEN '-2' THEN a.cancel_type ELSE '' END) AS cancelType, IFNULL(u.office_id,'') as officeId, u.user_id as userid FROM mtmydb.mtmy_orders a LEFT JOIN mtmydb.mtmy_users u ON a.user_id = u.user_id
LEFT JOIN mtmydb.mtmy_order_goods_mapping m ON a.order_id = m.order_id LEFT JOIN mtmydb.mtmy_goods g ON g.goods_id=m.goods_id LEFT JOIN mtmydb.mtmy_goods_category f ON f.category_id=g.category_id WHERE a.parent_id=0
AND u.nickname like '%'||#{username} ||'%' CONCAT('%', #{username} , '%')
AND u.mobile=#{mobile}
AND a.order_id = #{orderid}
AND a.order_status=#{orderstatus}
AND a.create_logo=#{createlogo}
AND a.channel_flag=#{channelFlag}
AND DATE_FORMAT(a.add_time,'%Y-%m-%d') >= DATE_FORMAT(#{begtime},'%Y-%m-%d')
AND DATE_FORMAT(a.add_time,'%Y-%m-%d') <= DATE_FORMAT(#{endtime},'%Y-%m-%d')
AND DATE_FORMAT(a.pay_time,'%Y-%m-%d') >= DATE_FORMAT(#{payBegTime},'%Y-%m-%d')
AND DATE_FORMAT(a.pay_time,'%Y-%m-%d') <= DATE_FORMAT(#{payEndTime},'%Y-%m-%d')
AND a.user_id = #{userid}
AND f.category_id=#{cetaid}
AND m.goods_id=#{goodsids}
AND f.category_id=#{cetaid} and m.goods_id=#{goodsids}
AND a.is_real=#{searchIsReal}
AND a.del_flag=#{userDelFlag}
GROUP BY a.order_id
ORDER BY ${page.orderBy}
ORDER BY a.add_time DESC

SELECT a.order_id, a.is_real as "strReal", a.channel_flag as "strChannel", c.order_amount as "strOrderAmount", c.total_amount as "strTotalAmount", a.total_amount as "orderTotalAmount", a.order_status as "status", a.shipping_type as "shipType", a.province as 'newProvince', a.city as 'newCity', a.district as 'newDistrict', a.address, a.consignee, a.mobile, a.pay_code, a.pay_name, a.shipping_code, a.shipping_name, a.shipping_time, a.temp_orderId, u.nickname as "username", c.goods_name, c.goods_sn, c.spec_key_name, c.goods_num as "strGoodsNum", c.goods_price as "strGoodsPrice", f.`name` as "catename", sp.goods_no as "goodsNo", sp.bar_code as "barCode", ogd.order_arrearage AS orderArrearage, a.user_note AS userNote, a.admin_note AS adminNote, a.is_real AS orderIsReal, a.add_time AS strAddTime, a.shipping_price as newShippingPrice, u.user_id as usersId, u.mobile as usersMobile, a.pay_time as payTime, (CASE a.order_status WHEN '-2' THEN a.cancel_type ELSE '' END) AS cancelType FROM mtmydb.mtmy_orders a LEFT JOIN mtmydb.mtmy_users u ON a.user_id=u.user_id LEFT JOIN mtmydb.mtmy_order_goods_mapping c ON c.order_id=a.order_id LEFT JOIN mtmydb.mtmy_goods g ON g.goods_id=c.goods_id LEFT JOIN mtmydb.mtmy_goods_category f ON f.category_id=g.category_id LEFT JOIN mtmydb.mtmy_spec_goods_price sp ON (g.goods_id = sp.goods_id and c.spec_key = sp.spec_key AND sp.del_flag = 0) LEFT JOIN (SELECT SUM(order_arrearage) AS order_arrearage,order_id FROM mtmydb.mtmy_order_goods_details GROUP BY order_id) ogd ON a.order_id = ogd.order_id ${sqlMap.dsf}
and a.parent_id=0
AND u.nickname like '%'||#{username} ||'%' CONCAT('%', #{username} , '%')
AND u.mobile=#{mobile}
AND a.order_id = #{orderid}
AND a.order_status=#{orderstatus}
AND a.create_logo=#{createlogo}
AND a.channel_flag=#{channelFlag}
AND DATE_FORMAT(a.add_time,'%Y-%m-%d') >= DATE_FORMAT(#{begtime},'%Y-%m-%d')
AND DATE_FORMAT(a.add_time,'%Y-%m-%d') <= DATE_FORMAT(#{endtime},'%Y-%m-%d')
AND DATE_FORMAT(a.pay_time,'%Y-%m-%d') >= DATE_FORMAT(#{payBegTime},'%Y-%m-%d')
AND DATE_FORMAT(a.pay_time,'%Y-%m-%d') <= DATE_FORMAT(#{payEndTime},'%Y-%m-%d')
AND f.category_id=#{cetaid}
AND c.goods_id=#{goodsids}
AND f.category_id=#{cetaid} and c.goods_id=#{goodsids}
AND a.is_real=#{searchIsReal}
AND a.del_flag=#{userDelFlag}
HAVING (orderarrearage > 0 OR orderarrearage IS NOT NULL)
HAVING (orderarrearage = 0 OR orderarrearage IS NULL)
ORDER BY ${page.orderBy}
ORDER BY a.add_time DESC

SELECT a.order_id, a.is_real as "strReal", a.channel_flag as "strChannel", c.order_amount as "strOrderAmount", c.total_amount as "strTotalAmount", a.total_amount as "orderTotalAmount", a.order_status as "status", a.shipping_type as "shipType", a.province as 'newProvince', a.city as 'newCity', a.district as 'newDistrict', a.address, a.consignee, a.mobile, a.pay_code, a.pay_name, a.shipping_code, a.shipping_name, a.shipping_time, a.temp_orderId, u.nickname as "username", c.goods_name, c.goods_sn, c.spec_key_name, c.goods_num as "strGoodsNum", c.goods_price as "strGoodsPrice", f.`name` as "catename", sp.goods_no as "goodsNo", sp.bar_code as "barCode", ogd.order_arrearage AS orderArrearage, a.user_note AS userNote, a.admin_note AS adminNote, a.is_real AS orderIsReal, a.add_time AS strAddTime, a.shipping_price as newShippingPrice, u.user_id as usersId, u.mobile as usersMobile, a.pay_time as payTime, (CASE a.order_status WHEN '-2' THEN a.cancel_type ELSE '' END) AS cancelType FROM mtmydb.mtmy_orders a LEFT JOIN mtmydb.mtmy_users u ON a.user_id=u.user_id LEFT JOIN mtmydb.mtmy_order_goods_mapping c ON c.order_id=a.order_id LEFT JOIN mtmydb.mtmy_goods g ON g.goods_id=c.goods_id LEFT JOIN mtmydb.mtmy_goods_category f ON f.category_id=g.category_id LEFT JOIN mtmydb.mtmy_spec_goods_price sp ON (g.goods_id = sp.goods_id and c.spec_key = sp.spec_key AND sp.del_flag = 0) LEFT JOIN (SELECT SUM(order_arrearage) AS order_arrearage,order_id FROM mtmydb.mtmy_order_goods_details GROUP BY order_id) ogd ON a.order_id = ogd.order_id
WHERE a.parent_id=0
AND u.nickname like '%'||#{username} ||'%' CONCAT('%', #{username} , '%')
AND u.mobile=#{mobile}
AND a.order_id = #{orderid}
AND a.order_status=#{orderstatus}
AND a.create_logo=#{createlogo}
AND a.channel_flag=#{channelFlag}
AND DATE_FORMAT(a.add_time,'%Y-%m-%d') >= DATE_FORMAT(#{begtime},'%Y-%m-%d')
AND DATE_FORMAT(a.add_time,'%Y-%m-%d') <= DATE_FORMAT(#{endtime},'%Y-%m-%d')
AND DATE_FORMAT(a.pay_time,'%Y-%m-%d') >= DATE_FORMAT(#{payBegTime},'%Y-%m-%d')
AND DATE_FORMAT(a.pay_time,'%Y-%m-%d') <= DATE_FORMAT(#{payEndTime},'%Y-%m-%d')
AND f.category_id=#{cetaid}
AND c.goods_id=#{goodsids}
AND f.category_id=#{cetaid} and c.goods_id=#{goodsids}
AND a.is_real=#{searchIsReal}
AND a.del_flag=#{userDelFlag}
HAVING (orderarrearage > 0 OR orderarrearage IS NOT NULL)
HAVING (orderarrearage = 0 OR orderarrearage IS NULL)
ORDER BY ${page.orderBy}
ORDER BY a.add_time DESC

mapper.update:
UPDATE mtmydb.mtmy_orders SET
shipping_code=#{shippingcode},
shipping_name=#{shippingname},
shipping_time=#{shippingtime},
return_time = #{returnTime}
where order_id=#{orderid}

UPDATE mtmydb.mtmy_orders SET
order_status=2
where order_id=#{orderid}

UPDATE mtmydb.mtmy_orders SET
order_status=#{orderstatus}
where order_id=#{parentid}

UPDATE mtmydb.mtmy_orders SET
order_status=#{orderstatus}
where parent_id=#{orderid}

UPDATE mtmydb.mtmy_orders SET
order_status=#{orderstatus},
order_amount=#{orderamount}
where
parent_id=#{parentid}

UPDATE 
	mtmydb.mtmy_orders 
SET 
	order_status = #{order_status}

		cancel_type = #{cancelType}

WHERE order_id = #{order_id} AND order_status = -1

UPDATE mtmydb.mtmy_user_accounts
SET account_balance = #{accountBalance}, 
	account_arrearage = #{accountArrearage},
	user_integral = #{userIntegral}
where user_id = #{userid}

UPDATE mtmydb.mtmy_orders SET pay_id = #{payid}, pay_code = #{paycode}, pay_name = #{payname}, order_status = #{orderstatus}, mobile = #{mobile}, consignee = #{consignee}, invoice_overtime = #{invoiceOvertime}, shipping_type = #{shippingtype}, user_note = #{usernote}
cancel_type = #{cancelType}
WHERE order_id = #{orderid}

mapper.select:
SELECT COUNT(*) 
FROM mtmydb.mtmy_order_invoice_relevancy
WHERE order_id = #{orderid}

SELECT a.order_id AS orderid, a.is_real as isReal, u.nickname AS "username", a.add_time AS addtime, a.pay_time AS paytime, a.pay_name AS payname, a.total_amount AS totalamount, a.order_arrearage AS orderArrearage, a.order_status AS orderstatus, a.is_comment AS isComment, a.distinction AS distinction FROM mtmydb.mtmy_orders a LEFT JOIN mtmydb.mtmy_users u ON a.user_id = u.user_id WHERE a.del_flag = 0 AND a.user_id=#{userid}
AND a.order_status=#{orderstatus}
AND a.distinction=#{distinction}
AND ( u.nickname like CONCAT('%', #{keyword} , '%') OR u.mobile like CONCAT('%', #{keyword} , '%') OR a.order_id like CONCAT('%', #{keyword} , '%') )
ORDER BY ${page.orderBy}
ORDER BY a.add_time DESC

SELECT COUNT(*)
FROM mtmydb.mtmy_orders
WHERE order_id = #{orderId}

