mapper.select:
SELECT a.order_id AS orderid, a.order_status AS orderstatus, p.pay_name AS payname, a.user_money AS usermoney, a.coupon_price AS couponprice, a.order_amount AS orderamount, a.total_amount AS totalamount, a.add_time AS addtime, a.action_id AS actionid, a.user_note AS userNote, a.admin_note AS adminnote, a.create_logo AS createlogo, a.postal_code AS postalcode, a.flag AS flag, a.mobile AS mobile, u.nickname AS "username", a.is_real as isReal, a.channel_flag AS channelflag, COUNT(m.rec_id) AS "goodsnum", ogd.order_arrearage AS orderArrearage FROM mtmydb.mtmy_orders a LEFT JOIN mtmydb.mtmy_users u ON a.user_id = u.user_id LEFT JOIN mtmydb.mtmy_payment p ON a.pay_id = p.pay_id LEFT JOIN (SELECT SUM(order_arrearage) AS order_arrearage,order_id FROM mtmydb.mtmy_order_goods_details GROUP BY order_id) ogd ON a.order_id = ogd.order_id LEFT JOIN mtmydb.mtmy_order_goods_mapping m ON a.order_id = m.order_id LEFT JOIN mtmydb.mtmy_goods g ON g.goods_id=m.goods_id LEFT JOIN mtmydb.mtmy_goods_category f ON f.category_id=g.category_id ${sqlMap.dsf} and a.parent_id=0 AND a.del_flag = 0
AND u.nickname like '%'||#{username} ||'%' CONCAT('%', #{username} , '%')
AND u.mobile=#{mobile}
AND a.order_id = #{orderid}
AND a.order_status=#{orderstatus}
AND a.create_logo=#{createlogo}
AND a.channel_flag=#{channelFlag}
AND DATE_FORMAT(a.add_time,'%Y-%m-%d') >= DATE_FORMAT(#{begtime},'%Y-%m-%d')
AND DATE_FORMAT(a.add_time,'%Y-%m-%d') <= DATE_FORMAT(#{endtime},'%Y-%m-%d')
AND a.user_id = #{userid}
AND f.category_id=#{cetaid}
AND m.goods_id=#{goodsids}
AND f.category_id=#{cetaid} and m.goods_id=#{goodsids}
AND a.is_real=#{searchIsReal}
GROUP BY a.order_id
HAVING orderarrearage > 0
HAVING orderarrearage = 0
ORDER BY ${page.orderBy}
ORDER BY a.add_time DESC

SELECT a.order_id, a.is_real as "strReal", a.channel_flag as "strChannel", c.order_amount as "strOrderAmount", c.total_amount as "strTotalAmount", a.order_status as "status", a.shipping_type as "shipType", a.address, a.consignee, a.mobile, a.pay_code, d.pay_name, a.shipping_code, a.shipping_name, a.shipping_time, a.temp_orderId, b.nickname as "username", c.goods_name, c.goods_sn, c.spec_key_name, c.goods_num as "strGoodsNum", c.goods_price as "strGoodsPrice", f.`name` as "catename", sp.goods_no as "goodsNo", sp.bar_code as "barCode", ogd.order_arrearage AS orderArrearage, a.user_note AS userNote, a.admin_note AS adminNote, a.is_real AS orderIsReal FROM mtmydb.mtmy_orders a LEFT JOIN mtmydb.mtmy_payment d ON a.pay_code=d.pay_code LEFT JOIN mtmydb.mtmy_users b ON a.user_id=b.user_id LEFT JOIN mtmydb.mtmy_order_goods_mapping c ON c.order_id=a.order_id LEFT JOIN mtmydb.mtmy_goods g ON g.goods_id=c.goods_id LEFT JOIN mtmydb.mtmy_goods_category f ON f.category_id=g.category_id LEFT JOIN mtmydb.mtmy_spec_goods_price sp ON (g.goods_id = sp.goods_id and c.spec_key = sp.spec_key) LEFT JOIN (SELECT SUM(order_arrearage) AS order_arrearage,order_id FROM mtmydb.mtmy_order_goods_details GROUP BY order_id) ogd ON a.order_id = ogd.order_id WHERE a.del_flag =0 and a.parent_id=0
AND b.nickname like '%'||#{username} ||'%' CONCAT('%', #{username} , '%')
AND b.mobile=#{mobile}
AND a.order_id = #{orderid}
AND a.order_status=#{orderstatus}
AND a.create_logo=#{createlogo}
AND DATE_FORMAT(a.add_time,'%Y-%m-%d') >= DATE_FORMAT(#{begtime},'%Y-%m-%d')
AND DATE_FORMAT(a.add_time,'%Y-%m-%d') <= DATE_FORMAT(#{endtime},'%Y-%m-%d')
AND a.user_id = #{userid}
AND f.category_id=#{cetaid}
AND c.goods_id=#{goodsids}
AND f.category_id=#{cetaid} and c.goods_id=#{goodsids}
GROUP BY a.order_id,d.pay_name,c.goods_name,c.goods_sn,c.spec_key_name,c.goods_num,c.goods_price,f.name,sp.goods_no,sp.bar_code,c.order_amount,c.total_amount
HAVING orderarrearage > 0
HAVING orderarrearage = 0
ORDER BY ${page.orderBy}
ORDER BY a.add_time DESC

mapper.update:
UPDATE mtmydb.mtmy_orders SET
order_status=#{orderstatus}
where order_id=#{parentid}

UPDATE mtmydb.mtmy_orders SET
order_status=#{orderstatus}
where parent_id=#{orderid}

UPDATE mtmydb.mtmy_orders SET
order_status=#{orderstatus},
order_amount=#{orderamount}
where
parent_id=#{parentid}

UPDATE mtmydb.mtmy_user_accounts
SET account_balance = #{accountBalance}, 
	account_arrearage = #{accountArrearage} 
where user_id = #{userid}

UPDATE mtmydb.mtmy_orders
	SET order_status = -2
WHERE order_id = #{orderid}
