mapper.select:
SELECT , m.goods_name AS 'goods.goodsName', a.name AS 'user.name', o1.name AS 'office.name' FROM mtmydb.mtmy_appt_order AS r LEFT JOIN trains.sys_office AS o1 ON r.shop_id = o1.id LEFT JOIN trains.sys_user AS a ON r.beautician_id = a.id LEFT JOIN mtmydb.mtmy_order_goods_mapping AS m ON r.goods_mapping_id = m.rec_id ${sqlMap.dsf}
AND m.order_id = #{orderId}
AND r.appt_status = #{apptStatus }
AND r.appt_date >= #{beginDate}
AND r.appt_date <= #{endDate}
AND ( r.user_name LIKE '%'||#{keyword}||'%' CONCAT('%', #{keyword}, '%') OR r.user_phone LIKE '%'||#{keyword}||'%' CONCAT('%', #{keyword}, '%') OR o1.name LIKE '%'||#{keyword}||'%' CONCAT('%', #{keyword}, '%') OR a.name LIKE '%'||#{keyword}||'%' CONCAT('%', #{keyword}, '%') )
ORDER BY r.appt_date DESC,r.appt_start_time DESC

mapper.update:
UPDATE mtmydb.mtmy_appt_order
SET 
	service_start_time = #{serviceStartTime},
	service_end_time = #{serviceEndTime}
WHERE 
	id = #{reservationId}
