mapper.select:
SELECT , m.goods_name AS 'goods.goodsName', a.name AS 'user.name', o.name AS 'office.name' FROM mtmydb.mtmy_appt_order AS r LEFT JOIN trains.sys_office AS o ON r.shop_id = o.id LEFT JOIN trains.sys_user AS a ON r.beautician_id = a.id LEFT JOIN mtmydb.mtmy_order_goods_mapping AS m ON r.goods_mapping_id = m.rec_id WHERE 1=1
AND r.appt_status = #{apptStatus }
AND r.appt_date BETWEEN #{beginDate} AND #{endDate}
AND ( r.user_name LIKE '%'||#{keyword}||'%' CONCAT('%', #{keyword}, '%') OR r.user_phone LIKE '%'||#{keyword}||'%' CONCAT('%', #{keyword}, '%') OR o.name LIKE '%'||#{keyword}||'%' CONCAT('%', #{keyword}, '%') OR a.name LIKE '%'||#{keyword}||'%' CONCAT('%', #{keyword}, '%') )
ORDER BY r.appt_date DESC,r.appt_start_time DESC
