mapper.update:
UPDATE mtmydb.mtmy_returned_goods SET
	return_status=#{returnStatus}
where id=#{id}

UPDATE mtmydb.mtmy_returned_goods SET
	return_status=#{returnStatus}
where id=#{id}

mapper.select:
SELECT

FROM mtmydb.mtmy_returned_goods a 
LEFT JOIN mtmydb.mtmy_order_goods_mapping b ON a.goods_mapping_id=b.rec_id
LEFT JOIN trains.sys_franchisee c ON a.office_id=c.id
LEFT JOIN mtmydb.mtmy_users d ON a.user_id=d.user_id
LEFT JOIN mtmydb.mtmy_pd_warehouse e ON e.id=a.warehouse_id
${sqlMap.dsf}	and a.del_flag=0

	AND d.nickname like
	'%'||#{userName} ||'%'
	CONCAT('%', #{userName} , '%')



	AND d.mobile=#{mobile}


	AND a.id = #{id}


	AND a.order_id = #{orderId}


	AND a.is_storage = #{isStorage}


	AND a.return_status = #{returnStatus}


	AND DATE_FORMAT(a.apply_date,'%Y-%m-%d') >= DATE_FORMAT(#{begtime},'%Y-%m-%d')


	AND DATE_FORMAT(a.apply_date,'%Y-%m-%d') <= DATE_FORMAT(#{endtime},'%Y-%m-%d')



		ORDER BY ${page.orderBy}


		ORDER BY  a.apply_date desc

SELECT
FROM mtmydb.mtmy_returned_goods a LEFT JOIN mtmydb.mtmy_order_goods_mapping b ON a.goods_mapping_id=b.rec_id LEFT JOIN trains.sys_franchisee c ON a.office_id=c.id LEFT JOIN mtmydb.mtmy_users d ON a.user_id=d.user_id LEFT JOIN mtmydb.mtmy_pd_warehouse e ON e.id=a.warehouse_id WHERE 1=1 AND a.del_flag=0 AND a.user_id =#{userId}
AND ( d.nickname LIKE CONCAT('%', #{keyword} , '%') OR d.mobile LIKE CONCAT('%', #{keyword} , '%') OR a.order_id LIKE CONCAT('%', #{keyword} , '%') )
AND a.id = #{id}
AND a.is_storage = #{isStorage}
AND a.return_status = #{returnStatus}
AND DATE_FORMAT(a.apply_date,'%Y-%m-%d') >= DATE_FORMAT(#{begtime},'%Y-%m-%d')
AND DATE_FORMAT(a.apply_date,'%Y-%m-%d') <= DATE_FORMAT(#{endtime},'%Y-%m-%d')
ORDER BY ${page.orderBy}
ORDER BY a.apply_date desc
