mapper.select:
SELECT
	COUNT(1)
FROM mtmydb.mtmy_returned_goods 
WHERE 
	return_status IN (11,12,13,14,15,16)
AND del_flag = 0 
AND order_id= #{orderId}
AND goods_mapping_id= #{goodsMappingId}
AND id != #{id}

mapper.update:
UPDATE mtmydb.mtmy_returned_goods_card SET
	return_num=(return_num + (#{returnNum}))
WHERE mapping_id=#{goodsMappingId}
AND returned_id = #{returnedId}

UPDATE mtmydb.mtmy_returned_goods SET
	return_status=#{returnStatus}
where id=#{id}

UPDATE mtmydb.mtmy_returned_goods SET
	return_status=#{returnStatus}
where id=#{id}

mapper.select:
SELECT a.id, a.order_id, a.goods_mapping_id, b.goods_id, b.goods_name as "goodsName", b.goods_num as "goodsNum", b.spec_key, b.spec_key_name as "specName", b.cost_price, b.market_price, b.goods_price as "goodsPrice", a.total_amount, b.order_amount as "orderAmount", b.order_balance, b.order_arrearage, b.service_times as "serviceTimes", a.user_id, d.nickname as "userName", d.mobile, b.is_real, a.problem_desc as "problemDesc", a.refusal_cause as "refusalCause", IFNULL(f.name,a.audit_by) AS "audit_by", a.audit_date, a.apply_type, a.return_num, a.apply_date, a.return_reason, a.return_amount, a.warehouse_id, a.is_storage, a.return_status, IFNULL(g.name,a.receipt_by) AS "receipt_by", IFNULL(j.name,a.financial_by) AS "financial_by", a.update_by, a.update_date, a.office_id, a.remarks, IFNULL(h.name,a.shipper_by) AS "shipper_by", a.consigner_name, a.consigner_address, a.consigner_shipping_name, a.consigner_shipping_code, a.consigner_shipping_time, a.consigner_shipping_img, a.shipping_name, a.shipping_code, a.shipping_time, a.shipper_date, e.`name` as "hoseName", e.governor, e.address, a.channel_flag AS 'channelFlag', a.receive_name, a.receive_account, a.return_type FROM mtmydb.mtmy_returned_goods a LEFT JOIN mtmydb.mtmy_order_goods_mapping b ON a.goods_mapping_id=b.rec_id LEFT JOIN trains.sys_franchisee c ON a.office_id=c.id LEFT JOIN mtmydb.mtmy_users d ON a.user_id=d.user_id LEFT JOIN mtmydb.mtmy_pd_warehouse e ON e.id=a.warehouse_id LEFT JOIN trains.sys_user f ON f.id=a.audit_by LEFT JOIN trains.sys_user g ON g.id=a.receipt_by LEFT JOIN trains.sys_user h ON h.id=a.shipper_by LEFT JOIN trains.sys_user j ON j.id=a.financial_by ${sqlMap.dsf} and a.del_flag=0
AND d.nickname like '%'||#{userName} ||'%' CONCAT('%', #{userName} , '%')
AND d.mobile=#{mobile}
AND a.id = #{returnedId}
AND a.order_id = #{orderId}
AND a.is_storage = #{isStorage}
AND a.return_status = #{returnStatus}
AND DATE_FORMAT(a.apply_date,'%Y-%m-%d') >= DATE_FORMAT(#{begtime},'%Y-%m-%d')
AND DATE_FORMAT(a.apply_date,'%Y-%m-%d') <= DATE_FORMAT(#{endtime},'%Y-%m-%d')
ORDER BY ${page.orderBy}
ORDER BY a.apply_date desc

SELECT

	FROM mtmydb.mtmy_returned_goods a 
LEFT JOIN mtmydb.mtmy_order_goods_mapping b ON a.goods_mapping_id=b.rec_id
LEFT JOIN trains.sys_franchisee c ON a.office_id=c.id
LEFT JOIN mtmydb.mtmy_users d ON a.user_id=d.user_id
LEFT JOIN mtmydb.mtmy_pd_warehouse e ON e.id=a.warehouse_id
LEFT JOIN trains.sys_office tso ON tso.id = a.office_id
${sqlMap.dsf}
AND a.del_flag=0 
AND a.user_id =#{userId}

	AND (
		locate(
			a.office_id,
			concat(tso.parent_ids, tso.id)
		) > 0
	)
	AND (
		locate(
			#{franchiseeId},
			concat(tso.parent_ids, tso.id)
		) > 0
	)


	AND (
	a.id LIKE
	CONCAT('%', #{keyword} , '%')
	OR a.order_id LIKE
	CONCAT('%', #{keyword} , '%')
	)


	AND a.id = #{id}


	AND a.is_storage = #{isStorage}


	AND a.return_status = #{returnStatus}


	AND DATE_FORMAT(a.apply_date,'%Y-%m-%d') >= DATE_FORMAT(#{begtime},'%Y-%m-%d')


	AND DATE_FORMAT(a.apply_date,'%Y-%m-%d') <= DATE_FORMAT(#{endtime},'%Y-%m-%d')



		ORDER BY ${page.orderBy}


		ORDER BY  a.apply_date desc
