mapper.select:
SELECT

FROM train_equipment e

${sqlMap.dsf}
AND e.del_flag = 0

	AND e.name like CONCAT('%', #{name}, '%')



		AND e.shop_id = #{shopId}


		AND e.bazaar_id = #{bazaarId}




		ORDER BY ${page.orderBy}


		ORDER BY e.create_date DESC

mapper.delete:
UPDATE train_equipment
SET del_flag = 1
WHERE id = #{equipmentId}

mapper.select:
SELECT COUNT(*) AS sum
FROM train_shop_equipment
WHERE shop_id = #{shopId}
AND equipment_id = #{equipmentId}

mapper.update:
UPDATE train_shop_equipment
SET is_enabled = #{isEnabled}
WHERE id = #{shopComEquipmentId}

mapper.select:
SELECT COUNT(*) AS num
FROM train_equipment
WHERE bazaar_id = #{bazaarId}
AND label_id = #{labelId}

SELECT ar.appt_date AS 'apptDate', o.name AS 'bazaarName', ar.time AS 'time', ar.shop_name FROM train_arrange_equipment ar LEFT JOIN sys_office o ON o.id = ar.local_bazaar_id WHERE ar.equipment_id = #{equipmentId}
ORDER BY ${page.orderBy}
ORDER BY ar.appt_date DESC
