mapper.update:
UPDATE mtmydb.mtmy_theme
SET del_flag = 1
WHERE id = #{themeId}

mapper.select:
SELECT mta.id AS 'themeMappingId', mta.theme_id AS 'themeId', c2.name AS 'goods.goodsCategory.name', c1.name AS 'goods.goodsCategory.parent.name', g.goods_id AS 'goods.goodsId', g.goods_name AS 'goods.goodsName' FROM mtmydb.mtmy_theme_mapping mta LEFT JOIN mtmydb.mtmy_goods g ON g.goods_id = mta.some_id LEFT JOIN mtmydb.mtmy_goods_category c2 ON c2.category_id = g.category_id LEFT JOIN mtmydb.mtmy_goods_category c1 ON c1.category_id = c2.parent_id WHERE mta.theme_id = #{themeId} AND g.del_flag = 0 AND c1.del_flag = 0 AND mta.type = 1
AND g.goods_id = #{newGoodsId}
AND g.goods_name like CONCAT('%', #{newGoodsName}, '%')
AND c2.category_id = #{newGoodsCategoryId}

mapper.insert:
INSERT INTO mtmydb.mtmy_theme_mapping(
	theme_id,
	type,
	some_id
)VALUES(
	#{themeId},
	#{type},
	#{someId}
)
