mapper.select:
SELECT c.content_id AS 'contentId', c.`name` AS 'contentName', c.lesson_id AS 'lessionId', l.`name` AS 'lessionName', (SELECT c.`name` FROM train_categorys c WHERE c.parent_id = 0 AND l.category_id = c.category_id GROUP BY c.category_id ) AS 'oneClassify', (SELECT tc.`name` FROM train_categorys tc WHERE tc.category_id = ( SELECT c.parent_id FROM train_categorys c WHERE parent_id = 0 AND c.category_id = l.category_id GROUP BY c.category_id) ) AS 'twoClassify', (SELECT COUNT(1) FROM train_lesson_favorites f WHERE f.lesson_id = c.lesson_id ) AS 'collect', CASE c.content_type WHEN 1 THEN '视频' WHEN 2 THEN '文档' WHEN 3 THEN '其他' END AS 'status' FROM train_lesson_contents c LEFT JOIN train_lessons l ON l.lesson_id = c.lesson_id LEFT JOIN train_categorys tc ON tc.category_id = l.category_id WHERE 1 = 1
AND c.`name` like '%${contentName}%'
AND c.lesson_id = #{lessionId}
AND l.`name` like '%${lessionName}%'
AND tc.category_id = #{oneClassify}
AND tc.parent_id = #{twoClassify}
