mapper.select:
SELECT s.id as id, s.`name` as sName, s.sex as sex, sr.`name` as srName, ubi.birthday as birthday, ubi.nation as nation, ubi.native as native, ue.probation_start_date as probationStartDate, s.del_flag as delFlag, o.name as oName, s.filing as filing, s.mobile as mobile, s.induction_time as inductionTime FROM sys_user s LEFT JOIN user_base_info ubi ON s.id = ubi.user_id LEFT JOIN sys_user_role sur ON s.id = sur.user_id LEFT JOIN sys_role sr ON sur.role_id = sr.id LEFT JOIN user_employed ue ON ue.user_id = s.id LEFT JOIN sys_office o ON o.id = s.office_id
${sqlMap.dsf} AND s.del_flag = #{parentDel}
AND s.id != '1'
AND s.name like CONCAT('%', #{sName}, '%')
AND s.filing = #{filing}
AND s.filing = #{filing}
AND s.filing = #{filing}
AND (o.id = #{officeId} OR o.parent_ids LIKE CONCAT('%,', #{officeId}, ',%'))
AND s.mobile = #{mobile}

mapper.update:
UPDATE sys_user SET 
	photo = #{photo},
	filing = #{filing},
	del_flag = #{delFlag}
WHERE id = #{id}
