mapper.update:
UPDATE quartz_schedulejob 
	SET job_status = #{jobStatus}
WHERE job_id = #{jobId}
	AND del_flag = 0

mapper.delete:
UPDATE quartz_schedulejob 
	SET del_flag = 1
WHERE job_id = #{jobId}

mapper.select:
SELECT id, job_name AS 'jobName', job_description AS 'jobDescription', start_date AS 'startDate', end_date AS 'endDate', run_time AS 'runTime', create_date AS 'createDate', status, LEFT(exception_msg,20) AS 'exceptionMsg', remarks FROM quartz_schedulejob_log WHERE 1 = 1
AND job_name = #{jobName}
AND status = #{status}
ORDER BY id DESC

SELECT id, job_name AS 'jobName', job_description AS 'jobDescription', start_date AS 'startDate', end_date AS 'endDate', run_time AS 'runTime', create_date AS 'createDate', status, LEFT(exception_msg,20) AS 'exceptionMsg', remarks FROM quartz_schedulejob_log WHERE 1 = 1
AND job_name = #{jobName}
AND status = #{status}
ORDER BY id DESC LIMIT 1
