mapper.select:
SELECT u.usernum, u.addtime FROM mtmydb.user_report as u WHERE 1=1
AND u.addtime BETWEEN #{begtime} AND #{endtime}
ORDER BY u.addtime ASC

SELECT g.category_id, g.name AS categoryName, SUM(g.goodsnum) AS goodsnum, SUM(g.amount) AS amount, SUM(o.ordernum) AS ordernum FROM mtmydb.category_goods_report g, mtmydb.category_order_report o WHERE g.category_id = o.category_id
AND o.addtime BETWEEN #{begtime} AND #{endtime}
AND g.parent_id = #{parentId }
GROUP BY g.category_id, g.`name` ORDER BY g.category_id
