mapper.select:
SELECT

FROM sys_office a

WHERE a.id = #{id}



SELECT

	,(SELECT COUNT(*) FROM sys_office AS B WHERE A.id=B.parent_id AND B.del_flag=0) AS 'num'
FROM sys_office a

WHERE a.del_flag = #{DEL_FLAG_NORMAL}

${sqlMap.dsf}
OR a.id = #{currentUser.office.id}

SELECT a.id, a.parent_id AS 'parent.id', a.parent_ids, a.CODE, a.NAME, a.type, a.grade, a.remarks, a.is_test AS 'isTest', ar.NAME AS 'area.name', (SELECT COUNT(1) FROM sys_office AS B WHERE B.parent_id = A.id AND B.del_flag = 0) AS 'num' FROM sys_office a LEFT JOIN sys_area ar ON ar.id = a.area_id WHERE 1 = 1
AND a.parent_ids LIKE #{parentIds}
or a.id=#{id }
AND a.del_flag = 0 ORDER BY a.code

mapper.update:
UPDATE sys_office SET parent_id = #{parent.id}, parent_ids = #{parentIds}, area_id = #{area.id}, franchisee_id = #{franchisee.id},
district = (SELECT id FROM sys_area WHERE CODE = (SELECT CODE FROM sys_area AS a WHERE a.`id` = #{area.id}) AND TYPE = 4 AND del_flag = 0), city = (SELECT id FROM sys_area WHERE CODE = LEFT((SELECT CODE FROM sys_area AS a WHERE a.`id` = #{area.id}),9) AND TYPE = 3 AND del_flag = 0), province = (SELECT id FROM sys_area WHERE CODE = LEFT((SELECT CODE FROM sys_area AS a WHERE a.`id` = #{area.id}),5) AND TYPE = 2 AND del_flag = 0),
code = #{code}, name = #{name}, type = #{type}, grade = #{grade}, level = (LENGTH( #{parentIds})-LENGTH(REPLACE( #{parentIds},',',''))), address = #{address}, zip_code = #{zipCode}, master = #{master}, phone = #{phone}, fax = #{fax}, email = #{email}, update_by = #{updateBy.id}, update_date = #{updateDate}, remarks = #{remarks}, longitude = #{longitude}, latitude = #{latitude}, useable=#{useable}, is_test = #{isTest}, primary_person=#{primaryPerson.id}, deputy_person=#{deputyPerson.id}, office_code=#{officeCode} WHERE id = #{id}

UPDATE sys_office SET 
	`level` = (LENGTH( #{parentIds})-LENGTH(REPLACE( #{parentIds},',',''))),
	parent_id = #{parent.id}, 
	parent_ids = #{parentIds}
WHERE id = #{id}

mapper.insert:
insert into sys_office_info( id, short_name, postal_code, contacts, telephone, store_phone, begin_time, end_time, detailed_address, img, tags, intro, bed_num, status, details, del_flag ) values ( #{id}, #{officeInfo.shortName}, #{officeInfo.postalCode}, #{officeInfo.contacts}, #{officeInfo.telephone}, #{officeInfo.storePhone}, #{officeInfo.beginTime}, #{officeInfo.endTime}, #{officeInfo.detailedAddress}, #{officeInfo.img}, #{officeInfo.tags}, #{officeInfo.intro}, #{officeInfo.bedNum}, #{officeInfo.status}, #{officeInfo.details}, #{DEL_FLAG_NORMAL} )ON DUPLICATE KEY UPDATE short_name = #{officeInfo.shortName}, postal_code = #{officeInfo.postalCode}, contacts = #{officeInfo.contacts}, telephone = #{officeInfo.telephone}, store_phone = #{officeInfo.storePhone}, begin_time = #{officeInfo.beginTime}, end_time = #{officeInfo.endTime}, detailed_address = #{officeInfo.detailedAddress}, img = #{officeInfo.img}, tags = #{officeInfo.tags}, intro = #{officeInfo.intro}, details = #{officeInfo.details}, bed_num = #{officeInfo.bedNum},
del_flag = #{DEL_FLAG_NORMAL}
del_flag = #{DEL_FLAG_DELETE}

mapper.update:
	UPDATE sys_office_info SET 
		del_flag = #{DEL_FLAG_DELETE}
    WHERE 
    1=1
    AND id = #{id}

mapper.select:
SELECT id AS upOfficeId, NAME AS upOfficeName FROM trains.`sys_office` WHERE code = #{upOfficeCode}
AND grade = #{grade}
AND del_flag = 0

SELECT a.id, a.parent_id AS "parent.id", a.parent_ids, a.area_id AS "area.id", a.code, a.name, a.sort, a.type, a.grade, a.address, a.zip_code, a.master, a.phone, a.fax, a.email, a.remarks, a.create_by AS "createBy.id", a.create_date, a.update_by AS "updateBy.id", a.update_date, a.del_flag, a.is_new, a.is_recommend AS isRecommend, a.longitude AS longitude, a.latitude AS latitude, a.useable AS useable, a.primary_person AS "primaryPerson.id", a.deputy_person AS "deputyPerson.id" FROM sys_office AS a ${sqlMap.dsf}
AND (a.parent_ids LIKE '%,'||#{id}||',%' CONCAT('%,', #{id}, ',%') OR a.id = #{id})
AND a.del_flag = #{DEL_FLAG_NORMAL}

mapper.update:
