mapper.select:
SELECT , CONCAT( (SELECT GROUP_CONCAT(`name`) FROM trains.sys_office WHERE id IN ( SELECT substring_index(substring_index(b.parent_ids,',',c.help_topic_id+2),',',-1) FROM trains.sys_user a LEFT JOIN trains.sys_office b ON a.office_id = b.id JOIN mysql.help_topic c ON c.help_topic_id < (length(b.parent_ids) - length(replace(b.parent_ids,',','')) - 1) WHERE a.id = #{id} ) ),',',o.name )AS parendNames
FROM sys_user a
WHERE a.id = #{id}

