SELECT a.id, a.login_name, a. NAME, a.phone, a.mobile, a.del_flag, a.create_date AS createDate, a.type, c. NAME AS "company.name", o. NAME AS "office.name" FROM sys_user a LEFT JOIN sys_franchisee c ON c.id = a.company_id LEFT JOIN sys_office o ON o.id = a.office_id
${sqlMap.dsf} AND a.type in ('yg','qy')
AND a.id != '1'
AND a.del_flag = #{parentDel }
AND a.company_id = #{company.id}
AND o.id = #{office.id} AND c.id = 1
AND a.login_name like CONCAT('%', #{loginName}, '%')
AND a.name like CONCAT('%', #{name}, '%')
AND a.mobile = #{mobile}
ORDER BY ${page.orderBy}
ORDER BY a.create_date desc, o.code,c.code, a.name

SELECT a.id, a.login_name, a.NAME, a.nickname, a.phone, a.mobile, a.del_flag, a.create_date AS createDate, CASE a.type WHEN 'pt' THEN '普通会员' WHEN 'syr' THEN '手艺人用户' WHEN 'yg' THEN '员工' WHEN 'qy' THEN '企业' ELSE '' END AS type, CASE a.type WHEN 'syr' THEN (SELECT m.mod_name FROM train_model m LEFT JOIN train_model_franchisee f ON m.id = f.mod_id WHERE f.user_id = a.id) WHEN 'qy' THEN (SELECT m.mod_name FROM train_model m LEFT JOIN train_model_franchisee f ON m.id = f.mod_id WHERE f.franchisee_id = a.company_id LIMIT 1) ELSE '普通会员' END AS modelName,
c. NAME AS "company.name", o. NAME AS "office.name" FROM sys_user a LEFT JOIN sys_franchisee c ON c.id = a.company_id LEFT JOIN sys_office o ON o.id = a.office_id
${sqlMap.dsf}
AND a.id != '1'
AND a.mobile = #{mobile}
AND a.type = #{type}
AND a.name like CONCAT('%', #{name}, '%')
ORDER BY ${page.orderBy}
ORDER BY a.create_date desc, o.code,c.code, a.name

SELECT DISTINCT a.id, a.login_name, a. NAME, a.phone, a.mobile, a.del_flag, c. NAME AS "company.name", o. NAME AS "office.name", o.code AS a, c.code AS b FROM sys_user a RIGHT JOIN (SELECT user_id FROM trains.sys_user_role WHERE role_id = (SELECT id FROM trains.sys_role WHERE enname = 'pb' AND del_flag = 0)) ur ON a.id = ur.user_id LEFT JOIN sys_franchisee c ON c.id = a.company_id LEFT JOIN sys_office o ON o.id = a.office_id LEFT JOIN train_user_skill us ON us.user_id = a.id
${sqlMap.dsf}
AND a.id != '1'
AND a.del_flag = #{parentDel }
AND (o.id = #{office.id} OR o.parent_ids LIKE CONCAT('%,', #{office.id}, ',%'))
AND (o.id = #{company.id} OR o.parent_ids LIKE CONCAT('%,', #{company.id}, ',%'))
AND a.login_name like CONCAT('%', #{loginName}, '%')
AND a.name like CONCAT('%', #{name}, '%')
AND us.skill_id = #{skill.id}
AND a.del_flag = 0
ORDER BY ${page.orderBy}
ORDER BY o.code,c.code, a.name

SELECT DISTINCT a.id, a.name, a.mobile, a.photo, o.id AS "office.id", o.parent_ids AS "office.parentIds", o. NAME AS "office.name", o.code AS a, c.code AS b FROM sys_user a RIGHT JOIN (SELECT user_id FROM trains.sys_user_role WHERE role_id = (SELECT id FROM trains.sys_role WHERE enname = 'pb' AND del_flag = 0)) ur ON a.id = ur.user_id LEFT JOIN sys_franchisee c ON c.id = a.company_id LEFT JOIN sys_office o ON o.id = a.office_id LEFT JOIN train_user_skill us ON us.user_id = a.id WHERE a.del_flag = 0
AND (o.id = #{office.id} OR o.parent_ids LIKE CONCAT('%,', #{office.id}, ',%'))
AND a.name like CONCAT('%', #{name}, '%')
AND a.mobile = #{mobile}
AND a.company_id = #{company.id}
ORDER BY ${page.orderBy}
ORDER BY o.code,c.code, a.name

SELECT
FROM sys_user a
WHERE a.del_flag = #{DEL_FLAG_NORMAL}
AND c.id = #{company.id}
AND o.id = #{office.id}
AND (o.id = '' or o.id is null)
AND a.id != '1'
AND a.login_name like '%'||#{loginName}||'%' CONCAT('%', #{loginName}, '%')
AND a.name like '%'||#{name}||'%' CONCAT('%', #{name}, '%')
${sqlMap.dsf}
ORDER BY a.name

SELECT
	a.id, a.name, a.login_name
FROM sys_user a
WHERE a.del_flag = #{DEL_FLAG_NORMAL}
	AND a.office_id = #{office.id}
ORDER BY a.name

