mapper.select:
SELECT u.id, u.name, (SELECT d.label FROM trains.sys_dict d WHERE d.value = u.user_type AND d.type='sys_user_type') AS 'positonName', IFNULL((SELECT de.name FROM trains.train_department de LEFT JOIN trains.train_department_position p ON p.department_id = de.id WHERE p.position_key = u.user_type AND u.company_id = de.office_id),'') AS 'departmentName', (SELECT de.id FROM trains.train_department de LEFT JOIN trains.train_department_position p ON p.department_id = de.id WHERE p.position_key = u.user_type AND u.company_id = de.office_id) AS 'departmentId', (SELECT o.name FROM trains.sys_office o WHERE o.id = u.office_id) AS 'officeName', u.mobile FROM trains.sys_user u WHERE u.del_flag = '0'
AND u.name like CONCAT('%', #{name}, '%')
AND u.id = #{id}

select ur.user_id from fzx_role r
left join fzx_user_role ur on ur.fzx_role_id = r.id
where r.enname = 'sjgly' and ur.user_id is not null and r.mod_id = #{mode_id}

SELECT DISTINCT ur.user_id FROM fzx_role_menu rm INNER JOIN fzx_user_role ur ON ur.fzx_role_id = rm.fzx_role_id LEFT JOIN fzx_role r ON r.id = rm.fzx_role_id WHERE rm.fzx_menu_id IN
#{value}
AND r.mod_id = #{modId}
