mapper.select:
SELECT a.id AS articleId, a.category_id AS categoryId, a.author_name AS authorName, a.author_photo AS authorPhoto, a.title, a.is_recommend AS isRecommend, a.is_show AS isShow, a.is_top AS isTop, a.sort, a.audit_flag AS auditFlag, a.is_task AS isTask, a.task_date AS taskDate, a.del_flag, c.name AS "category.name", (SELECT COUNT(1) FROM train_articles_comment AS b WHERE b.article_id=a.id AND b.del_flag = 0) AS commentNum FROM train_articles AS a LEFT JOIN train_articles_category c ON a.category_id = c.id WHERE 1=1
AND a.del_flag = #{delFlag}
AND a.audit_flag = #{auditFlag} AND a.del_flag = #{DEL_FLAG_NORMAL}
AND a.category_id = #{categoryId}
AND a.title like '%'||#{title}||'%' CONCAT('%', #{title}, '%')
AND a.create_date BETWEEN #{beginDate} AND #{endDate}
ORDER BY a.is_top,a.sort DESC,a.create_date DESC

SELECT

FROM train_articles AS a
WHERE id = #{articleId}

SELECT a.comment_id, a.article_id, a.create_name, a.create_photo, a.create_date, a.content, a.like_num, b.title, c.id FROM train_articles_comment a LEFT JOIN train_articles b ON a.article_id = b.id LEFT JOIN train_articles_category c ON b.category_id = c.id WHERE a.del_flag = #{delFlag} AND b.del_flag = 0 AND c.del_flag = 0
AND b.title LIKE '%'||#{articleName}||'%' CONCAT('%', #{articleName}, '%')
AND c.id = #{categoryId}
AND a.create_date >= #{beginDate}
AND a.create_date <= #{endDate}
ORDER BY a.create_date DESC

mapper.update:
UPDATE train_articles_comment SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE comment_id = #{commentId}

UPDATE train_articles SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{articleId}

UPDATE train_articles_category SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{categoryId}
