mapper.insert:
INSERT INTO train_exercises_lesson_mapping(
	exercise_id,
	lesson_Id,
	CATEGORY_ID,
	CREATETIME,
	STATUS
) VALUES (
	#{exerciseId},
	#{lessonId},
	#{categoryId},
	SYSDATE(),
	#{status}
) ON DUPLICATE KEY UPDATE CREATETIME = SYSDATE(), STATUS=0;

mapper.select:
SELECT EXERCISE_ID FROM TRAIN_EXERCISES_LESSON_MAPPING WHERE status=0 and  

		LESSON_ID = #{lessonId}


		CATEGORY_ID = #{categoryId}

mapper.update:
UPDATE TRAIN_EXERCISES_LESSON_MAPPING SET 
	STATUS = '-1'
	WHERE 

		LESSON_ID = #{lessonId}


		CATEGORY_ID = #{categoryId}

	and STATUS=0

mapper.select:
SELECT a.exercise_id AS exerciseId, a.exercise_type AS exerciseType, a.exercise_title AS exerciseTitle, b.name FROM trains.`train_exercises` AS a,trains.`train_categorys` AS b WHERE a.`category_id`=b.`category_id` AND a.exercise_type = #{lessontype}
AND a.category_id = #{categoryId }
ORDER BY RAND() LIMIT #{newNum}

SELECT  COUNT(*) FROM trains.`train_exercises` AS e,trains.`train_categorys` AS c
	WHERE e.exercise_type = #{lessontype } 

		AND e.category_id = #{categoryId } 

	AND e.STATUS = '0'
	AND e.category_id = c.category_id AND c.STATUS = '0'
