mapper.select:
SELECT e.*, c.name FROM trains.train_exercises e LEFT JOIN train_exercises_lesson_mapping m ON e.exercise_id = m.exercise_id LEFT JOIN train_categorys c ON e.category_id = c.category_id WHERE m.status = 0
AND m.LESSON_ID = #{lessonId}
AND m.CATEGORY_ID = #{categoryId}
GROUP BY e.exercise_id ORDER BY e.CREATETIME DESC

mapper.update:
UPDATE 
	trains.train_exercises_lesson_mapping 
SET 
	status = -1 
WHERE 
	exercise_id = #{exerciseId} 

	AND lesson_id = #{lessonId}


	AND category_id = #{categoryId}
