mapper.select:
SELECT  FROM `handbook_type` m where m.`del_flag`=0

	AND m.type = #{type}

		AND m.is_shop = #{isShop}

mapper.update:
UPDATE handbook_type SET 
	`name` = #{name},
	`sort` = #{sort},
	AND icon_url = #{iconUrl}  
	`update_by` = #{updateBy.id},
	`update_date` = #{updateDate}
WHERE id = #{id}
