mapper.select:
SELECT c.id , c.value, c.label, c.type, c.description, c.sort, c.remarks, c.del_flag FROM sys_dict c WHERE c.value NOT IN
#{value}
AND c.type = 'sys_user_type' AND c.del_flag = 0

mapper.insert:
INSERT INTO train_department_position(
	department_id,
	position_key
) VALUES(
	#{department.dId},
	#{value}
);
