mapper.select:
SELECT ro.order_id as orderId, ro.temp_order_id as tempOrderId, ro.order_type as orderType, ro.arrearage_price as arrearagePrice, ro.arrearage_office as arrearageOffice, ro.amount as amount, DATE_FORMAT(ro.add_time,'%Y-%m-%d %H:%i:%s') as addTime, ro.order_status as orderStatus, ro.pay_code as payCode, DATE_FORMAT(ro.pay_time,'%Y-%m-%d %H:%i:%s') as payTime, ro.charge_id as chargeId, ro.user_id as userId, ro.user_mobile as userMobile, ro.user_office_id as userOfficeId, ro.channel_flag as channelFlag, o.name as arrearageOfficeName, u.name as userName, f.name as franchiseeName, ro.billmonth, ro.serialnumber, ro.bankaccount, ro.openbank, ro.openname, ro.proof, ro.explains FROM train_refund_order ro left join sys_user u on u.id = ro.user_id left join sys_office o on o.id = arrearage_office left join sys_franchisee f on f.id = o.franchisee_id where 1=1
and order_id = #{orderId}
order by ro.add_time desc

mapper.update:
update train_refund_order
set order_status = #{status}

	,remarks=#{remarks}

where order_id = #{order_id}

update sys_office_statement a,train_statement_order b 
set a.refund_order_status = #{status}
where a.id = b.statement_id and b.order_id = #{order_id}
