mapper.select:
SELECT ro.order_id as orderId, ro.temp_order_id as tempOrderId, ro.order_type as orderType, ro.arrearage_price as arrearagePrice, ro.arrearage_office as arrearageOffice, ro.amount as amount, DATE_FORMAT(ro.add_time,'%Y-%m-%d %H:%i:%s') as addTime, ro.order_status as orderStatus, ro.pay_code as payCode, DATE_FORMAT(ro.pay_time,'%Y-%m-%d %H:%i:%s') as payTime, ro.charge_id as chargeId, ro.user_id as userId, ro.user_mobile as userMobile, ro.user_office_id as userOfficeId, ro.channel_flag as channelFlag, u.name as userName, o.name as arrearageOfficeName,
f.name as franchiseeName, f.id AS franchiseeId,
ro.explains, ro.remarks FROM train_refund_order ro left join sys_office o on o.id = arrearage_office left join sys_user u on u.id = ro.user_id left join sys_franchisee f on f.id = o.franchisee_id where 1=1 AND ro.order_status !=1
and order_id = #{orderId}
and ro.order_status = #{orderStatus}
and ro.order_type = #{orderType}
and ro.arrearage_office = #{arrearageOffice}
AND o.franchisee_id = #{franchiseeId}
order by ro.order_status,ro.add_time desc

SELECT ro.order_id as orderId, o.name as arrearageOfficeName, ro.arrearage_price as arrearagePrice, ro.amount as amount, CASE ro.order_status WHEN 1 THEN '待支付' WHEN 2 THEN '待审核' WHEN 3 THEN '已入账' WHEN 4 THEN '已驳回' ELSE '取消订单' END AS orderStatus, u.name AS userName,
CASE ro.pay_code WHEN 'wx' THEN '微信' WHEN 'alipay' THEN '支付宝' ELSE '转账' END AS payCode, refs.bankaccount, refs.openbank, refs.openname, group_concat(stat.order_id) AS orderCode, oaccount.credit_limit - oaccount.used_limit AS usedLimit,
DATE_FORMAT(ro.add_time,'%Y-%m-%d %H:%i:%s') as addTime, ro.explains FROM train_refund_order ro left join sys_office o on o.id = ro.arrearage_office LEFT JOIN sys_user u ON u.id= ro.user_id LEFT JOIN train_statement_order sod ON sod.order_id=ro.order_id LEFT JOIN sys_office_statement stat ON stat.id= sod.statement_id LEFT JOIN train_refund_serialnumber refs ON refs.order_id = ro.order_id LEFT JOIN sys_office_account oaccount ON oaccount.office_id = ro.arrearage_office where 1=1 AND ro.order_status !=1
and ro.order_id = #{orderId}
and ro.order_status = #{orderStatus}
and ro.order_type = #{orderType}
GROUP BY ro.order_id ORDER BY ro.order_id,ro.add_time desc

mapper.update:
update train_refund_order
set order_status = #{status}

	,remarks=#{remarks}

where order_id = #{order_id}

update sys_office_statement a,train_statement_order b 
set a.refund_order_status = #{status}
where a.id = b.statement_id and b.order_id = #{order_id}
