mapper.select:
SELECT DISTINCT t.category_id AS 'categoryId', t.parent_id AS 'parentId', t.name, t.priority, t.cover_pic AS 'coverPic', t.introduce, t.cate_type AS 'cateType', t.office_code AS 'officeCode', t.createuser, t.status, T.CREATETIME, t.is_show AS isShow, t.sort, so.name AS "office.name" FROM train_categorys t LEFT JOIN sys_office so ON t.office_id = so.id
${sqlMap.dsf}
AND t.priority = #{priority}
AND t.parent_id = #{parentId}
AND t.status != -1 ORDER BY t.sort DESC,T.CREATETIME DESC

SELECT DISTINCT t.category_id AS 'categoryId', t.parent_id AS 'parentId', t.name, t.priority, t.cover_pic AS 'coverPic', t.introduce, t.cate_type AS 'cateType', t.office_code AS 'officeCode', t.createuser, t.status, T.CREATETIME, t.is_show AS isShow, t.sort, so.name AS "office.name" FROM train_categorys t LEFT JOIN sys_office so ON t.office_id = so.id
${sqlMap.dsf}
AND t.priority = #{priority}
AND t.office_id = #{franchisee.id}
AND t.parent_id = "0" AND t.status != -1 ORDER BY t.sort DESC,T.CREATETIME DESC

mapper.insert:
INSERT INTO train_categorys ( category_id, parent_id, name, priority, cover_pic, introduce, cate_type, office_code, createuser, status, createtime, updatetime, office_id, sort, is_show ) VALUES ( #{categoryId}, #{parentId}, #{name}, #{priority}, #{coverPic}, #{introduce}, #{cateType}, #{officeCode}, #{createuser}, #{status}, SYSDATE(), NULL,
#{franchisee.id}
#{office.id} , #{sort}, #{isShow} )

mapper.select:
SELECT DISTINCT t.category_id AS 'categoryId', t.parent_id AS 'parentId', t.name, t.priority, t.cover_pic AS 'coverPic', t.introduce, t.cate_type AS 'cateType', t.office_code AS 'officeCode', t.createuser, t.status, (SELECT COUNT(*) FROM train_categorys AS c WHERE t.category_id = c.parent_id AND c.status != -1) AS 'num', T.CREATETIME, t.sort, t.is_show AS isShow, so.name AS "office.name" FROM train_categorys t LEFT JOIN sys_office so ON t.office_id = so.id
${sqlMap.dsf}
AND t.parent_id = #{parentId }
AND t.name LIKE '%'||#{name}||'%' CONCAT('%', #{name}, '%')
AND t.status != -1 ORDER BY t.priority,t.sort DESC,t.createtime

SELECT DISTINCT
t.name FROM train_categorys as t WHERE t.status != -1 AND t.category_id = #{categoryId}
a.name AS lessonName FROM train_lessons as a WHERE a.status=0 AND a.lesson_id = #{lessonId}

