mapper.select:
SELECT a.user_id AS 'user.mtmyUserId',b.name AS 'user.name',b.mobile AS 'user.mobile',c.name AS 'user.office.name',COUNT(1) AS gtpNnm FROM train_gtp_relations a LEFT JOIN sys_user b ON a.user_id = b.mtmy_user_id LEFT JOIN sys_office c ON b.office_id = c.id AND c.del_flag = 0
${sqlMap.dsf}
AND (c.id = #{office.id} OR c.parent_ids LIKE CONCAT('%,', #{office.id}, ',%'))
AND b.name like CONCAT('%', #{user.name}, '%')
AND b.mobile = #{user.mobile}
GROUP BY a.user_id,b.name,b.mobile,c.name ORDER BY gtpNnm DESC

SELECT b.name AS 'user.name',a.mtmy_user_name AS mtmyUserName,a.mobile,d.reg_time AS 'users.regtime' FROM train_gtp_relations a LEFT JOIN sys_user b ON a.user_id = b.mtmy_user_id LEFT JOIN sys_office c ON b.office_id = c.id AND c.del_flag = 0 LEFT JOIN mtmydb.mtmy_users d ON a.mtmy_user_id = d.user_id WHERE 1 = 1 AND a.user_id = #{mtmyUserId }
AND DATE_FORMAT(d.reg_time,'%Y-%m-%d') >= DATE_FORMAT(#{beginDate},'%Y-%m-%d')
AND DATE_FORMAT(d.reg_time,'%Y-%m-%d') <= DATE_FORMAT(#{endDate},'%Y-%m-%d')
