mapper.select:
SELECT id AS 'trainLiveActivityRatioId', name, is_show AS 'isShow', create_by AS 'createBy.id', create_date AS 'createDate', update_by AS 'updateBy.id', update_date AS 'updateDate', del_flag AS 'delFlag' FROM trains.train_live_activity_ratio WHERE del_flag = '0'
ORDER BY ${page.orderBy}
ORDER BY create_date DESC

mapper.update:
UPDATE trains.train_live_exchange_ratio
SET del_flag = '1'
WHERE activity_id = #{trainLiveActivityRatioId}

UPDATE trains.train_live_activity_ratio
	SET 
	  is_show = #{isShow}
WHERE id = #{trainLiveActivityRatioId}

UPDATE trains.train_live_exchange_ratio
SET is_show = #{isShow}
WHERE id = #{exchangeRatioId}

UPDATE trains.train_live_exchange_ratio
SET del_flag = '1'
WHERE id = #{exchangeRatioId}

mapper.select:
SELECT COUNT(*)
FROM trains.train_live_exchange_ratio
WHERE exchange_type = #{exchangeType}
AND activity_id = #{activityId}
AND is_show = '0'
