mapper.select:
SELECT
FROM trains.train_live_playback a LEFT JOIN trains.train_live_audit d ON a.auditId=d.id LEFT JOIN trains.sys_user b ON a.userId=b.id LEFT JOIN trains.sys_dict c ON c.`value`=b.user_type AND c.type = 'sys_user_type' WHERE 1=1
AND b.name like '%'||#{userName}||'%' CONCAT('%',#{userName}, '%')
AND a.name like '%'||#{name}||'%' CONCAT('%',#{name}, '%')
AND a.auditId=#{auditId}
AND a.playbackId=#{playbackId}
AND d.company_id ${sqlMap.dsf}
ORDER BY a.id DESC

mapper.update:
UPDATE train_live_playback SET
	is_show=#{isShow}
WHERE id=#{id}
