mapper.select:
SELECT u.name AS 'name', u.mobile AS 'phone', o.name AS 'organization.name', lo.order_id AS 'orderId', lo.integrals, lo.spec_price AS 'specPrice', lo.pay_date AS 'payDate', lo.pay_code AS 'payCode' FROM train_live_order lo LEFT JOIN sys_user u ON u.id = lo.user_id LEFT JOIN sys_office o ON o.id = u.office_id WHERE lo.type = 3 AND lo.order_status = 3
AND u.name like CONCAT('%', #{name}, '%')
AND u.mobile = #{phone}
AND (o.id = #{organization.id} OR o.parent_ids LIKE CONCAT('%,', #{organization.id}, ',%'))
AND DATE_FORMAT(lo.pay_date,'%Y-%m-%d') >= DATE_FORMAT(#{beginTime},'%Y-%m-%d')
AND DATE_FORMAT(lo.pay_date,'%Y-%m-%d') <= DATE_FORMAT(#{endTime},'%Y-%m-%d')
AND lo.order_id = #{orderId}
ORDER BY ${page.orderBy}
ORDER BY lo.pay_date DESC

SELECT u.name AS 'name', u.mobile AS 'phone', o.name AS 'organization.name', lo.order_id AS 'orderId', lo.integrals, lo.spec_price AS 'specPrice', lo.create_date AS 'createDate', lo.pay_code AS 'payCode' FROM train_live_order lo LEFT JOIN sys_user u ON u.id = lo.user_id LEFT JOIN sys_office o ON o.id = u.office_id WHERE lo.type = 4
AND u.name like CONCAT('%', #{name}, '%')
AND u.mobile = #{phone}
AND (o.id = #{organization.id} OR o.parent_ids LIKE CONCAT('%,', #{organization.id}, ',%'))
AND DATE_FORMAT(lo.create_date,'%Y-%m-%d') >= DATE_FORMAT(#{beginTime},'%Y-%m-%d')
AND DATE_FORMAT(lo.create_date,'%Y-%m-%d') <= DATE_FORMAT(#{endTime},'%Y-%m-%d')
AND lo.order_id = #{orderId}
ORDER BY ${page.orderBy}
ORDER BY lo.create_date DESC
