mapper.insert:
INSERT INTO trains.train_franchisee_advert(
	create_franise_id,
	banner_id,
	so_franise_id
) VALUES(
	#{createFranchisee},
	#{bannerId},
	#{soFranchisee}
)

mapper.select:
SELECT a.ad_id AS adId, a.ad_name AS adName, a.ad_type AS adType, a.ad_pic AS adPic, a.sort, a.del_flag, c.id AS "dict.id", c.label AS "dict.label", c.type AS "dict.type", c.description AS "dict.description", c.sort AS "dict.sort", c.parent_id AS "dict.parentId", c.create_by AS "dict.createBy", c.create_date AS "dict.createDate", c.update_by AS "dict.updateBy", c.update_date AS "dict.updateDate", c.remarks AS "dict.remarks", c.del_flag AS "dict.delFlag", f.banner_id AS "franchiseeBanner.bannerId", f.create_franise_id AS "franchiseeBanner.createFranchisee", f.so_franise_id AS "franchiseeBanner.soFranchisee" FROM train_advert a LEFT JOIN train_franchisee_advert f ON a.ad_id = f.banner_id LEFT JOIN sys_dict c ON a.ad_type = c.value WHERE c.type = 'banner_type' AND c.del_flag = 0
AND f.create_franise_id = #{user.company.id}
GROUP BY a.ad_id ORDER BY sort DESC

mapper.update:
UPDATE train_advert
	SET 
	  del_flag = #{flag }
	WHERE ad_id = #{adId}
