mapper.update:
UPDATE train_apply set
 `status`=#{status}
 , remarks = #{remarks}
WHERE id = #{id} AND user_id=#{userid}

UPDATE sys_user SET 
	`type` = #{type}
	
	
	,company_id = #{franchid},
	office_id = #{franchid} 
	
WHERE id = #{userid}

mapper.insert:
INSERT INTO `trains`.`fzx_user_role` ( 
	`user_id`,
	`fzx_role_id`
)
VALUES
	( 
		#{userid},
		(SELECT id FROM fzx_role WHERE mod_id = #{modid} AND del_flag =0  LIMIT 1)
	);

mapper.select:
SELECT tap.id, u.`name`, u.nickname, u.mobile, u.type, tap.user_id, tap.apply_type AS applyType, tap.audit_type AS auditType, tap. STATUS, mf.id AS mfId, mf.auth_end_date AS authEndDate, mf.mod_id FROM train_apply tap LEFT JOIN sys_user u ON tap.user_id = u.id LEFT JOIN train_model_franchisee mf ON mf.user_id = tap.user_id WHERE tap.`status`=3 AND tap.audit_type = 'syr'
AND u.mobile = #{mobile}
AND mf.mod_id = #{modId}
AND u.name like CONCAT('%', #{name}, '%')
ORDER BY tap.create_date DESC
