mapper.update:
UPDATE train_activity_course SET
is_on_sale = #{isyesno},
update_by = #{userId}, update_date = SYSDATE() WHERE id = #{acId}

mapper.select:
SELECT a.goods_name AS goodsName, b.name AS "user.name", b.mobile AS "user.mobile", b.sex AS "user.sex", a.pay_time AS payTime, a.amount FROM train_order a LEFT JOIN sys_user b ON a.user_id = b.id WHERE 1 = 1 AND a.order_status = 2 AND a.order_type = 1
AND a.goods_name like '%'||#{goodsName}||'%' CONCAT('%', #{goodsName}, '%')
AND b.name like '%'||#{user,name}||'%' CONCAT('%', #{user.name}, '%')
AND b.mobile = #{user.mobile}
AND a.pay_time > #{beginDate}
AND a.pay_time < #{lastDate}
ORDER BY a.pay_time DESC
